import React from "react";
class Todo extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            all_tasks : []
        }
        this.listofTasks()
    }

    listofTasks =async () => {
        var data =await fetch("http://127.0.0.1:8000/api/")
        var response = await data.json()

        this.setState({
            all_tasks : response
        })

    }

    deleteTask = async (Task) => {
       var data =await fetch(`http://127.0.0.1:8000/delete_task/${Task.id}`)
        this.listofTasks()
    }

    createTask = async (e) =>{
        e.preventDefault()
        var task =document.getElementById("task").value
        var formData = new FormData()
        formData.append("title", task)
        formData.append("completed", false)
        formData.append("id", null)
        console.log(formData)
        var data =await fetch(`http://127.0.0.1:8000/create_task/`, {
            method:"POST",
            body:formData
        })
        this.listofTasks()
    }



    render() {
        return (
                <div className="page-content page-container" id="page-content">

                    <div className="padding">
                        <div className="row container d-flex justify-content-center">
                            <div className="col-lg-12">
                                <div className="card px-3">
                                    <div className="card-body">
                                        <center><h2 className="">Awesome Todo list</h2>

                                        </center>

                                        <form onSubmit={this.createTask}>
                                            <div className="add-items d-flex"><input type="text" id="task"
                                                                                     className="form-control todo-list-input"
                                                                                     placeholder="What do you need to do today?"/>
                                                <button
                                                    className="add btn btn-primary font-weight-bold todo-list-add-btn">Add
                                                </button></div>
                                        </form>

                                        <div className="list-wrapper">
                                            <ul className="d-flex flex-column-reverse todo-list">
                                                {
                                                    this.state.all_tasks.map((list) => {
                                                        var Class = list.completed == true?"completed":""
                                                        return (
                                                            <li className={Class}>
                                                                <div className="form-check">
                                                                    <label className="form-check-label">
                                                                        <input className="checkbox" type="checkbox"/>{list.title}<i
                                                                                className="input-helper"></i>
                                                                    </label>
                                                                </div>
                                                                <i className="remove mdi mdi-close-circle-outline"  onClick={() => this.deleteTask(list)}></i>
                                                            </li>


                                                        )
                                                    })
                                                }

                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
            </div>
        );
    }

}

export default Todo
